import React, { Component } from 'react';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Form from './Form';
import NFTCard from './Card'


class App extends Component {
  constructor(props){
    super(props);
    this.state={
      response: {},
      NFTs:     [],
    }
  }

  handleResponse = (response) =>{
    this.setState({response:  response});
    this.setState({NFTs:      JSON.parse(response["data"])["data"]});
  }

  render() {
    return (
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h4" component="h1" style={{"textAlign":"center"}} gutterBottom>
            NFT Searcher using WebSockets
          </Typography>
        </Box>
        <Form getResponse={this.handleResponse} />
        <div className="row">
          {
            this.state.NFTs.map(elm => <NFTCard key={elm["blockNumber"]} NFT={elm}  sx={{ my: 10 }} className="col-4" />)
          }
        </div>
      </Container>
    );
  }
}

export default App;
