import React, { Component } from 'react';
import Button from '@mui/material/Button';
import { w3cwebsocket as W3CWebSocket } from "websocket";
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
const WAValidator = require('./wav');

//a list of available blockchains on moralis api
const availableChains=[{label:"Ethereum",symbol:"eth"},{label:"Binance Smart Chain",symbol:"bsc"},{label:"Polygon",symbol:"matic"},{label:"Solana",symbol:"sol"},{label:"Elrond",symbol:"egld"}];
class Form extends Component {

    constructor(props) {
      super(props);
      this.state = {
        chain: '',
        address:'caca',
        isError:{error:false}
      };
  
      this.handleSubmit = this.handleSubmit.bind(this);
    }

  
    handleSubmit(event) {
      alert('A form was submitted: ' + this.state.chain + ' // ' + this.state.address);
      event.preventDefault();
    }
    render() {
      return (
        <div>
          <form onSubmit={this.handleSubmit}>
            <Autocomplete
                disablePortal
                id="chain"
                onChange={(event, selectedValue) => {
                    //null check in case the user wants to press x in order to delete current selection
                    this.setState({chain:selectedValue!=null?selectedValue["symbol"]:''});
                    console.log(selectedValue["symbol"],this.state.address);
                    //im not sure if state can be null but a safety check won't hurt
                    if(this.state && selectedValue["symbol"]!=null && this.state.address!=null && !WAValidator.validate(this.state.address, "bsc")){
                        //user selected a network and wrote an address but they are wrong, let's give a warning
                        this.setState({isError:{error:true}});
                        console.log(this.state.isError);
                    }
                  }}
                options={availableChains}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Select network" />}
            />
            <TextField id="standard-basic" label="Standard" variant="standard" {...this.state.isError}/>
            <Button variant="outlined" type="submit" value="Submit" >Search</Button>
          </form>
        </div>
      )
    }
  }
  
export default Form;
