import React, { Component } from 'react';
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';



class ExpandMore extends Component{
    constructor(props){
        super(props);
        this.state={
            expand: false,
        }
    }

    render(){
        return(
            <IconButton onClick={this.setState({exapnd:!this.state.expand})}>
                <ExpandMoreIcon style={({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  })}/>
            </IconButton>
        )
    }
}

class NFTCard extends Component{
    constructor(props){
        super(props);
        this.state = {
            isExpanded  :false,
            NFT         :this.props.NFT,
        };
        console.log(this.state.NFT);
    }
    render(){
        return(
            <Card sx={{ maxWidth: 345 }}>
            <CardHeader
                title={this.state.NFT["name"]}
                subheader={this.state.NFT["blockNumber"]}
            />
            <CardMedia
                component="img"
                height="194"
                src={this.state.NFT["imageURL"]}
                alt={this.state.NFT["name"]}
            />
            <CardContent>
                <Typography variant="body2" color="text.secondary">
                    {this.state.NFT["contractType"]}
                </Typography>
            </CardContent>
            <CardActions disableSpacing>
            </CardActions>
            <Collapse in={this.state.isExpanded} timeout="auto" unmountOnExit>
                <CardContent>
                <Typography paragraph>Method:</Typography>
                <Typography paragraph>
                    {this.state.NFT["description"]}
                </Typography>
                <Typography paragraph>
                        Symbol : {this.state.NFT["symbol"]}
                </Typography>
                </CardContent>
            </Collapse>
            </Card>
        );
    }
}

export default NFTCard;
