import React, { Component } from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import { w3cwebsocket as W3CWebSocket } from "websocket";
import Container from '@mui/material/Container';

class Form extends Component {
    constructor(props) {
      super(props);
      this.state = {
        chain: '',
        address:''
      };
  
      this.handleChange = this.handleInputChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
    
    handleInputChange(event) {
      const target = event.target;
      const value = target.type === 'checkbox' ? target.checked : target.value;
      const chain = target.chain;
      
      this.setState({
        [chain]: value
      });
      console.log('Change detected. State updated' + chain + ' = ' + value);
    }
  
    handleSubmit(event) {
      alert('A form was submitted: ' + this.state.chain + ' // ' + this.state.address);
      event.preventDefault();
    }
  
    render() {
      return (
        <div>
          <form onSubmit={this.handleSubmit} >
            <div classchain="form-group">
              <label htmlFor="chainImput">chain</label>
              <input type="text" chain="chain" value={this.state.chain} onChange={this.handleChange} className="form-control" id="chainImput" placeholder="chain" />
            </div>
            <div classchain="form-group">
              <label htmlFor="addressImput">chain</label>
              <input chain="address" type="address" value={this.state.address} onChange={this.handleChange} className="form-control" id="addressImput" placeholder="address@domain.com" />
            </div>
            <Button variant="outlined"  type="submit" value="Submit" >Outlined</Button>
          </form>
        </div>
      )
    }
  }
  
export default Form;
