class Form extends Component {
    
    handleInputChange(event) {
      const target = event.target;
      const value = target.type === 'checkbox' ? target.checked : target.value;
      const chain = target.chain;
      
      this.setState({
        [chain]: value
      });
      console.log('Change detected. State updated' + chain + ' = ' + value);
    }
  
    handleSubmit(event) {
      alert('A form was submitted: ' + this.state.chain + ' // ' + this.state.address);
      event.preventDefault();
    }
  
    render() {
      return (
        <div>
          <form onSubmit={this.handleSubmit} >
            <div classchain="form-group">
              <label for="chainImput">chain</label>
              <input type="text" chain="chain" value={this.state.chain} onChange={this.handleChange} className="form-control" id="chainImput" placeholder="chain" />
            </div>
            <div classchain="form-group">
              <label for="addressImput">chain</label>
              <input chain="address" type="address" value={this.state.address} onChange={this.handleChange} className="form-control" id="addressImput" placeholder="address@domain.com" />
            </div>
            <input type="submit" value="Submit" className="btn btn-primary" />
          </form>
        </div>
      )
    }
  }
  
export default Form;
