import React, { Component } from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import { w3cwebsocket as W3CWebSocket } from "websocket";
import Container from '@mui/material/Container';
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
import { ClassNames } from '@emotion/react';

//a list of available blockchains on moralis api
const availableChains=[{label:"Ethereum",symbol:"eth"},{label:"Binance Smart Chain",symbol:"bsc"},{label:"Polygon",symbol:"matic"},{label:"Solana",symbol:"sol"},{label:"Elrond",symbol:"egld"}];
class Form extends Component {

    constructor(props) {
      super(props);
      this.state = {
        chain: '',
        address:'',
        isError:{Error:true}
      };
  
      this.handleChange = this.handleInputChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleInputChange(event) {
      const target = event.target;
      console.log(target.innerHTML);
      const value = target.type === 'checkbox' ? target.checked : target.value;
      const chain = target.chain;
      
      this.setState({
        [chain]: value
      });
      console.log('Change detected. State updated' + chain + ' = ' + value);
    }
  
    handleSubmit(event) {
      alert('A form was submitted: ' + this.state.chain + ' // ' + this.state.address);
      event.preventDefault();
    }
    render() {
      return (
        <div>
          <form onSubmit={this.handleSubmit}>
            <Autocomplete
                disablePortal
                id="chain"
                onChange={(event, selectedValue) => {
                    //null check in case the user wants to press x in order to delete current selection
                    this.setState({chain:selectedValue!=null?selectedValue["symbol"]:''});
                  }}
                options={availableChains}
                sx={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Select network" />}
            />
            <TextField id="standard-basic" label="Standard" variant="standard" {...this.state.isError}/>
            <Button variant="outlined" type="submit" value="Submit" >Search</Button>
          </form>
        </div>
      )
    }
  }
  
export default Form;
