import React, { Component } from 'react';
import Modal                from '@mui/material/Modal';
import Box                  from '@mui/material/Box';
import Container            from '@mui/material/Container';
import Typography           from '@mui/material/Typography';
import Form                 from './Form';
import NFTCard              from './Card';


const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      response:     {},
      NFTs:         [],
      isSucessful:  false
    }
  }

  handleResponse = (response) =>{
    const rspv = JSON.parse(response["data"]);
    if(rspv["isSuccessful"]){
      this.setState({response:      response});
      this.setState({isSuccessful:  true});
      this.setState({NFTs:          JSON.parse(response["data"])["data"]});
    }else{
      this.setState({isSuccessful:  false});
    }
  }

  render() {
    return (
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h4" component="h1" style={{"textAlign":"center"}} gutterBottom>
            NFT Searcher using WebSockets
          </Typography>
        </Box>
        <Form getResponse={this.handleResponse} />
        <div className="row">
          {(()=>{
            if(this.state.isSucessful){
              return(this.state.NFTs.map(elm => <NFTCard key={elm["blockNumber"]} NFT={elm}  sx={{ my: 10 }} className="col-4" />));
            }else{
              return(
                <Modal
                  open={!this.state.isSucessful}
                  //not a smart idea but it works for this case, we don't have more functionality so nothing is affected by this
                  onClose={this.setState({isSucessful : !this.state.isSucessful})}
                  aria-labelledby="modal-modal-title"
                  aria-describedby="modal-modal-description"
                >
                <Box style={style}>
                  <Typography id="modal-modal-title" variant="h6" component="h2">
                    Text in a modal
                  </Typography>
                  <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                    Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                  </Typography>
                </Box>
              </Modal>);
            }
          })}
        </div>
      </Container>
    );
  }
}

export default App;
